sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var requestType = g_form.getValue('Request_type'); // Replace 'Request_type' with the actual field name
    var whatNeeded = g_form.getControl('What Needed'); // Replace 'What Needed' with the actual field name

    // Clear all the options from the 'What Needed' field.
    whatNeeded.innerHTML = '';

    if (requestType === 'Human Resources') {
        addOption(whatNeeded, 'hr1', 'Human Resources 1');
        addOption(whatNeeded, 'hr2', 'Facilities 2');
        addOption(whatNeeded, 'other', 'Other');
    } else if (requestType === 'Facilities') {
        addOption(whatNeeded, 'fac1', 'Facilities 1');
        addOption(whatNeeded, 'fac2', 'Facilities 2');
        addOption(whatNeeded, 'other', 'Other');
    } else if (requestType === 'Legal') {
        addOption(whatNeeded, 'legal1', 'Legal 1');
        addOption(whatNeeded, 'legal2', 'Legal 2');
        addOption(whatNeeded, 'other', 'Other');
    }
    // Add more conditions for other 'Request Type' values if needed.
}

function addOption(select, value, text) {
    var option = document.createElement('option');
    option.value = value;
    option.text = text;
    select.appendChild(option);
}
